import React from 'react';
import { Table, Form, Tag, Button } from 'element-react';
import 'element-theme-default';
import storage from './storage';

class UserTable extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            columns: [
                {
                    type: 'index'
                },
                {
                    type: 'expand',
                    label: "详情",
                    expandPannel: function (data) {
                        return (
                            <Form>
                                <Form.Item label="姓名"><span>王小虎</span></Form.Item>
                                <Form.Item label="年龄"><span>23</span></Form.Item>
                                <Form.Item label="性别"><span>男</span></Form.Item>
                                <Form.Item label="头像"><span>头像</span></Form.Item>
                            </Form>
                        )
                    }
                },
                {
                    label: "姓名",
                    prop: "name",
                    width: 180,
                    render: function (data) {
                        return <Tag>{data.name}</Tag>
                    }
                },
                {
                    label: "操作",
                    prop: "address",
                    render: function () {
                        return (
                            <span>
                                <Button plain={true} type="info" size="small">编辑</Button>
                                <Button type="danger" size="small">删除</Button>
                            </span>
                        )
                    }
                }
            ],
            data: [{
                name: '王小虎',
                age: '23',
                sex: '男',
                img: '头像',
            }]
        }
    }

    //生命周期函数 页面加载就会触发
    componentDidMount() {
        //获取本地缓存的用户列表
        let userList = storage.get("UserList");
        if (userList) {
            this.setState({
                data: userList
            })
        }
    }

    render() {
        return (
            <div>
                <Table
                    style={{ width: '500px'}}
                    columns={this.state.columns}
                    data={this.state.data}
                    border={true}
                    highlightCurrentRow={true}
                    onCurrentChange={item => { console.log(item) }}
                />
            </div>
        )
    }
}
export default UserTable;
