import React from 'react';
import { Button, Dialog, Input, Tag, Table, Form } from 'element-react';
import 'element-theme-default';
import storage from './storage';

class UserList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            dialogVisible: false,
            list: [],
            finishList: [
                /*  {
                      title:"录制java",
                      checked:true
                  },
                  {
                      title:"录制react",
                      checked:false
                  },
                  {
                      title:"录制python",
                      checked:true
                  }*/
            ]
        };
    }

    //生命周期函数    页面加载就会触发
    componentDidMount() {
        //获取缓存的数据
        let todoList = storage.get("TodoList");
        if (todoList) {
            this.setState({
                finishList: todoList
            })
        }
    }
    //点击新增后显示表单弹窗

    addData = (e) => {
        if (e.keyCode == 13) {
            let title = this.refs.title.value;
            let tempList = this.state.finishList;
            tempList.push({
                title: title,
                checked: false
            })
            this.setState({
                list: tempList
            });
            //增加框设置为空
            this.refs.title.value = "";
            //缓存数据,使用localStorage，而将一个对象转为字符串使用JSON.stringify()函数
            storage.set("TodoList", tempList);
        }
    }

    changeState = (key) => {
        let templist = this.state.finishList;
        templist[key].checked = !templist[key].checked;
        this.setState({
            list: templist
        });
        storage.set("TodoList", templist);
    }
    deleteData = (key) => {
        let templist = this.state.finishList;
        templist.splice(key, 1);
        this.setState({
            list: templist
        })
        storage.set("TodoList", templist);
    }

    render() {
        return (
            <div>
                <Button type="primary" onClick={() => this.setState({ dialogVisible: true })}>新增</Button>
                <Dialog
                    size="tiny"
                    title="新增用户"
                    visible={this.state.dialogVisible}
                    onCancel={() => this.setState({ dialogVisible: false })}
                >
                    <Dialog.Body>
                        <Form model={this.state.form}>
                            <Form.Item label="姓名" labelWidth="40">
                                <Input placeholder="请输入姓名" />
                            </Form.Item>
                            <Form.Item label="年龄" labelWidth="40">
                                <Input placeholder="请输入年龄" />
                            </Form.Item>
                            <Form.Item label="性别" labelWidth="40">
                                <Input placeholder="请输入性别" />
                            </Form.Item>
                            <Form.Item label="上传头像">

                            </Form.Item>
                        </Form>
                    </Dialog.Body>

                    <Dialog.Footer className="dialog-footer">
                        <Button onClick={() => this.setState({ dialogVisible: false })}>取 消</Button>
                        <Button type="primary" onClick={() => this.setState({ dialogVisible: false })}>确 定</Button>
                    </Dialog.Footer>
                </Dialog>
                {
                    this.state.finishList.map((value, key) => {
                        if (value.checked == false) {
                            return (
                                <li key={key}>
                                    <input type="checkbox" checked={value.checked} onChange={this.changeState.bind(this, key)} />{value.title}
                                    ---<button onClick={this.deleteData.bind(this, key)}>删除</button>
                                </li>
                            )
                        }
                    })
                }
                <h2>已完成事项</h2>
                {
                    this.state.finishList.map((value, key) => {
                        if (value.checked == true) {
                            return (
                                <li key={key}>
                                    <input type="checkbox" checked={value.checked} onChange={this.changeState.bind(this, key)} />{value.title}
                                    ---<button onClick={this.deleteData.bind(this, key)}>删除</button>
                                </li>
                            )
                        }
                    })
                }
                <hr />
            </div>
        )
    }
}
export default UserList;
