import React from 'react';
import { Button, Dialog, Input, Form } from 'element-react';
import 'element-theme-default';
import storage from './storage';

class UserForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userList: {},
            dialogVisible: false
        };
    }

    //提交表单
    addUser = () => {
        //let title = this.refs.title.value;
        //获取缓存中的用户列表 todo：如果为空？
        let userList = storage.get("UserList");
        userList.push({
            name: '',
            age: '',
            sex: '',
            img: ''
        })
        this.setState({
            userList: userList,
            dialogVisible: false
        });
        //更新缓存中用户列表
        storage.set("UserList", userList);

    }

    render() {
        return (
            <div>
                <Button type="primary" onClick={() => this.setState({ dialogVisible: true })}>新增</Button>
                <Dialog
                    size="tiny"
                    title="新增用户"
                    visible={this.state.dialogVisible}
                    onCancel={() => this.setState({ dialogVisible: false })}
                >
                    <Dialog.Body>
                        <Form model={this.state.form}>
                            <Form.Item label="姓名" labelWidth="40">
                                <Input placeholder="请输入姓名" />
                            </Form.Item>
                            <Form.Item label="年龄" labelWidth="40">
                                <Input placeholder="请输入年龄" />
                            </Form.Item>
                            <Form.Item label="性别" labelWidth="40">
                                <Input placeholder="请输入性别" />
                            </Form.Item>
                            <Form.Item label="上传头像">

                            </Form.Item>
                        </Form>
                    </Dialog.Body>

                    <Dialog.Footer className="dialog-footer">
                        <Button onClick={() => this.setState({ dialogVisible: false })}>取 消</Button>
                        <Button type="primary" onClick={this.addUser}>确 定</Button>
                    </Dialog.Footer>
                </Dialog>
            </div>

        )
    }
}
export default UserForm;
