import React from 'react';
import { Button, Dialog, Input, Form } from 'element-react';
import 'element-theme-default';
import storage from './storage';

class UserForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            form: {
                name: '',
                age: '',
                sex: '',
                img: ''
            },
            userList: {},
            dialogVisible: false
        };
    }

    //提交表单
    addUser = () => {
        let userList = storage.get("UserList") || [];
        userList.push({
            name: this.form.name,
            age: this.form.age,
            sex: this.form.sex,
            img: this.form.img
        })
        this.setState({
            userList: userList,
            dialogVisible: false
        });
        //更新缓存中用户列表
        storage.set("UserList", userList);
    }
    onChange(key, value) {
        this.setState({
          form: Object.assign({}, this.state.form, { [key]: value })
        });
    }

    render() {
        return (
            <div>
                <Button type="primary" onClick={() => this.setState({ dialogVisible: true })}>新增</Button>
                <Dialog
                    size="tiny"
                    title="新增用户"
                    visible={this.state.dialogVisible}
                    onCancel={() => this.setState({ dialogVisible: false })}
                >
                    <Dialog.Body>
                        <Form ref="form" model={this.state.form}>
                            <Form.Item label="姓名" labelWidth="40" prop="name">
                                <Input placeholder="请输入姓名" />
                            </Form.Item>
                            <Form.Item label="年龄" labelWidth="40" prop="age">
                                <Input placeholder="请输入年龄" />
                            </Form.Item>
                            <Form.Item label="性别" labelWidth="40" prop="sex">
                                <Input placeholder="请输入性别" />
                            </Form.Item>
                            <Form.Item label="上传头像" prop="img">

                            </Form.Item>
                        </Form>
                    </Dialog.Body>

                    <Dialog.Footer className="dialog-footer">
                        <Button onClick={() => this.setState({ dialogVisible: false })}>取 消</Button>
                        <Button type="primary" onClick={this.addUser}>确 定</Button>
                    </Dialog.Footer>
                </Dialog>
            </div>

        )
    }
}
export default UserForm;
